        var types = [
            vocab.rdf.Property,
            vocab.owl.AnnotationProperty,
            vocab.owl.DatatypeProperty,
            vocab.owl.ObjectProperty
          ];
        var v = rdf.NodeFactory.createVar('_x_');
        var exprVar = new sparql.ExprVar(v);
